mapper.select:
       SELECT
		  count(1)
		FROM
			ic_project_package p where p.status >= 0 

		        and p.name like #{map.name}


		       and  p.status = #{map.status}

SELECT p.id, p.imgUrl, p.name, p.status, p.create_time, p.update_time, p.description, p.total_amount, p.total_count, p.max_annualized_rate, p.min_annualized_rate, p.max_borrow_period, p.max_borrow_period_type, p.min_borrow_period, p.min_borrow_period_type, p.progress, p.sale_complated_time, p.sale_start_time, p.sale_end_time FROM ic_project_package p where p.status >= 0
and p.name like #{map.name}"%"
and p.status = #{map.status}
order by p.id desc LIMIT ${map.startRow}, ${map.pageSize}

select *  from ic_project_package p  where  p.progress!=100

    and p.status=${status}

 order by p.name
